mapper.select:
SELECT TP2.ID, TP2.POST_TITLE, TP2.POST_CONTENT, TP2.MEMBER_ID, TP2.UPDATED_DATE, TP2.MEMBER_NICKNAME, TP2.POST_VIEW, PROFESSOR_MAJOR, RESEARCH_FIELD, RESEARCH_PROFIT, RESEARCH_PERIOD, RESEARCH_DEADLINE, RESEARCH_START_DATE, RESEARCH_SCHEDULE, RESEARCH_REQUIREMENT FROM ( SELECT ROWNUM R, TP1.ID, TP1.POST_TITLE, TP1.POST_CONTENT, TP1.MEMBER_ID, TP1.UPDATED_DATE, TP1.MEMBER_NICKNAME, TP1.POST_VIEW, PROFESSOR_MAJOR, RESEARCH_FIELD, RESEARCH_PROFIT, RESEARCH_PERIOD, RESEARCH_DEADLINE, RESEARCH_START_DATE, RESEARCH_SCHEDULE, RESEARCH_REQUIREMENT FROM ( SELECT TP.ID, P.POST_TITLE, P.POST_CONTENT, P.MEMBER_ID,P.UPDATED_DATE, M.MEMBER_NICKNAME, P.POST_VIEW, PROFESSOR_MAJOR, RESEARCH_FIELD, RESEARCH_PROFIT, RESEARCH_PERIOD, RESEARCH_DEADLINE, RESEARCH_START_DATE, RESEARCH_SCHEDULE, RESEARCH_REQUIREMENT FROM TBL_MEMBER M JOIN TBL_POST P ON M.ID = P.MEMBER_ID JOIN TBL_THESIS_POST TP ON P.ID = TP.ID ORDER BY TP.ID DESC ) TP1
WHERE ROWNUM <= #{endRow}
) TP2
WHERE R >= #{startRow}

mapper.update:
UPDATE TBL_POST
SET POST_VIEW = POST_VIEW + 1
WHERE ID = #{id}
