mapper.select:
SELECT ID, USER_EMAIL, USER_ID
FROM TBL_USER
WHERE (USER_ID = #{userId} OR USER_EMAIL = #{userEmail}) AND USER_PASSWORD = #{userPassword}

SELECT ID, USER_EMAIL, USER_ID
FROM TBL_USER
WHERE ID = #{id}

SELECT ID, USER_EMAIL, USER_ID
FROM TBL_USER
WHERE USER_EMAIL = #{userEmail}

mapper.update:
UPDATE TBL_USER
SET USER_PASSWORD = #{userPassword}
WHERE USER_EMAIL = #{userEmail}

UPDATE TBL_USER
SET USER_EMAIL = #{userEmail}, USER_NAME = #{userName}
WHERE ID = #{id}
